const mainMenu=document.querySelector(".main-menu"),userMenu=document.querySelector(".user-menu"),openMenuButton=document.querySelector(".nav-toggle-off"),popup=document.querySelector(".modal"),overlay=document.querySelector(".overlay");document.querySelector(".nav-toggle").addEventListener("click",e=>{mainMenu.classList.toggle("main-menu--active"),userMenu.classList.toggle("user-menu--active"),e.target.classList.toggle("nav-toggle--close")}),document.querySelector(".bestseller__order-button").addEventListener("click",()=>{popup.classList.add("modal--active"),overlay.classList.add("overlay--active")}),document.querySelector(".overlay").addEventListener("click",()=>{popup.classList.remove("modal--active"),overlay.classList.remove("overlay--active")}),popup.addEventListener("click",e=>{e.stopPropagation()}),document.querySelector(".size-select").addEventListener("click",e=>{e.preventDefault(),e.target.classList.toggle("size__link--active")});